function diff($t1, $t2)
{
  if(count(explode('L',$t1))>1)
    return 0;
  
  if(count(explode('L',$t2))>1)
    return 0;

  $p1 = explode(':',$t1);
  if(count($p1) == 1)
  {
    $t1 = "0:".sprintf("%06.3f",$t1);
    $p1 = explode(':',$t1);
  }
  $p2 = explode(':',$t2);
  if(count($p2) == 1)
  {
    $t2 = "0:".sprintf("%06.3f",$t2);
    $p2 = explode(':',$t2);
  }
  
  $mdiff = $p2[0]-$p1[0];
  $sdiff = $p2[1]-$p1[1];
  //echo "[$mdiff] [$sdiff] ---    ";
  
  if ($mdiff == 0 && $sdiff == 0)
    return 0;
  else if($mdiff == 1 && $sdiff < 0)
    return sprintf("%+07.3f",$sdiff+60);
  else if($mdiff > 1 && $sdiff < 0)
    return sprintf("%+07.3f",$sdiff+60);
  else if($mdiff == 0 && $sdiff > 0)
    return sprintf("%+06.3f",$sdiff);
  else if($mdiff == 0 && $sdiff < 0)
    return sprintf("%+06.3f",$sdiff);
  else if ($mdiff < 0 && $sdiff < 0)
    return "$mdiff:" . sprintf("%06.3f",-1*$sdiff);
  else if ($mdiff == -1 && $sdiff > 0)
    return sprintf("%06.3f",-(60-$sdiff));
  else if ($mdiff < -1 && $sdiff > 0)
    return ($mdiff+1) .":" . sprintf("%06.3f",60-$sdiff);
  else
    return "+" . $mdiff .":". sprintf("%06.3f",$sdiff);;
}

function do_post_request($url, $data, &$log, $optional_headers = null)
{
  $params = array('http' => array(
              'method' => 'POST',
              'content' => $data
            ));
  if ($optional_headers !== null) {
    $params['http']['header'] = $optional_headers;
  }
  //print_r($params);
  $ctx = stream_context_create($params);
  $fp = @fopen($url, 'rb', false, $ctx);
  echo $http_response_header[0];

  if (!$fp) {
    //throw new Exception("Problem with $url, $php_errormsg <br>");
  }
  $response = @stream_get_contents($fp);
  if ($response === false) {
    //throw new Exception("Problem reading data from $url, $php_errormsg <br>");
  }
  return $response;
}

function dbquery($query)
{
  $result = mysql_query($query) or die("<b>Error with MySQL Query:</b>.\n<br />Query: " . $query . "<br />\nError: (" . mysql_errno() . ") " . mysql_error());
  return $result;
}

